import React, { useContext } from 'react';
import './Projects.css';
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/css';
import Quiz from './src/quiz.png';
import Weather from 'src/images/weather-dashboard.png';
import Planner from 'src/images/day_planner.png';
import { themeContext } from '../../Context';
const Projects = () => {
  const theme = useContext(themeContext);
  const darkMode = theme.state.darkMode;
  return (
    <div className="projects" id="projects">
      {/* heading */}
      <span style={{ color: darkMode ? 'white' : '' }}>Projects</span>

      {/* slider */}
      <Swiper spaceBetween={30} slidesPerView={3} grabCursor={true} className="portfolio-slider">
        <SwiperSlide>
          <img src={Quiz} alt="" />
        </SwiperSlide>
        <SwiperSlide>
          <img src={Weather} alt="" />
        </SwiperSlide>
        <SwiperSlide>
          <img src={Planner} alt="" />
        </SwiperSlide>
      </Swiper>
    </div>
  );
};

export default Projects;
